import styled from "styled-components";
import bg from "../../assets/images/landing.jpg";

export const Container = styled.div`
  background: #528124;
  color: #fff;
  height: 100vh;
  display: flex;
  justify-content: center;
  justify-items: center;
  align-content: center;
  align-items: center;

  .rows {
    display: grid;
    grid-template-columns: 1fr 1fr;
    height: 100%;
    width: 100%;
  }
  .leftside {
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: left;
    align-content: center;
    align-items: left;
    background-color: white;
    width: 100%;
    height: 100%;
    padding: 0 100px;
    margin-left: 0;
    margin-right: 0;
    text-align: left;
    border-right: 1rem solid #528124;

    img {
      text-align: left;
      align-content: left;
      align-items: left;
    }
  }

  .rightside {
    background: url(${bg});
    filter: brightness(30%);
    background-position: center;
    background-size: cover;
    /* background-color: #528124; */
    width: 100%;
    height: 100%;
  }

  h2 {
    color: black;
    font-weight: 200;
    font-size: 4rem;
    line-height: 4rem;
    margin-top: 2rem;
  }
`;

export const Button = styled.div`
  width: 50%;
  margin-right: auto;
  margin-left: 0;
  font-family: "Poppins", sans-serif;
  font-weight: 600;
  font-size: 1rem;
  text-align: center;
  line-height: 1.2rem;
  margin-top: 2rem;
  background-color: #b9060d;
  width: fit-content;
  padding: 1rem 4rem;
  border-radius: 2rem;
  cursor: pointer;
  text-align: left;
  color: white;
  justify-content: left;

  :hover {
    background-color: #fff;
    color: #528124;
    transition: 0.2s ease-in-out;
  }
`;
