import styled from "styled-components";

export const Section = styled.section``;

export const Container = styled.div`
  h3 {
    color: #b9060d;
    margin-top: 1rem;
  }
  p {
    font-size: 1rem;
    @media screen and (max-width: 480px) {
      font-size: 0.8rem;
    }
  }
  img {
    max-height: 300px;
    border-radius: 1rem;
    margin: 1rem 0rem;
    display: flex;
    align-content: center;
    align-items: center;
    justify-content: center;
    justify-items: center;

    @media screen and (max-width: 480px) {
      display: block;
      width: 100%;
      border-radius: 1rem;
      margin: 2rem 0rem;
    }
  }
`;
