import styled from "styled-components";

export const Section = styled.section``;
export const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  h3 {
    text-align: center;
    color: #bc1212;
    padding-top: 2rem;
    font-size: 2rem;
  }

  p {
    text-align: center;
  }
`;
export const Icons = styled.div``;
