import styled from "styled-components";
import bg from "../../assets/images/landing.jpg";

export const Container = styled.div`
  background: #528124;
  color: #fff;
  height: 100vh;
  display: flex;
  justify-content: center;
  justify-items: center;
  align-content: center;
  align-items: center;

  .rows {
    display: grid;
    grid-template-columns: 1fr 1fr;
    height: 100%;
    width: 100%;
  }
  .leftside {
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: left;
    align-content: center;
    align-items: left;
    background-color: white;
    width: 100%;
    height: 100%;
    padding: 0 100px;
    margin-left: 0;
    margin-right: 0;
    text-align: left;

    img {
      text-align: left;
      align-content: left;
      align-items: left;
    }
  }

  .rightside {
    background: url(${bg});
    filter: brightness(30%);
    background-position: center;
    background-size: cover;
    /* background-color: #528124; */
    width: 100%;
    height: 100%;
  }

  h2 {
    color: black;
    font-weight: 200;
    font-size: 4rem;
    line-height: 4rem;
    margin-top: 2rem;
  }
`;
