import styled from "styled-components";

export const Container = styled.div`
  background: #528124;
  color: #fff;
  height: 100vh;
  display: flex;
  justify-content: center;
  justify-items: center;
  align-content: center;
  align-items: center;

  .rows {
    display: grid;
    grid-template-columns: 1fr 1fr;
    height: 100%;
    width: 100%;
  }
  .leftside {
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-content: center;
    align-items: center;
    background-color: white;
    width: 100%;
    height: 100%;
  }

  .rightside {
    width: 100%;
    height: 100%;
  }

  h2 {
    color: black;
    font-weight: 200;
    font-size: 4rem;
  }
`;
